mapper.update:
update ws_file_pub
set

    content = #{nextInfo.content},


    commit_info = #{nextInfo.commitInfo},


    status = #{nextInfo.status},


    file_content_id = #{nextInfo.fileContentId},

update_time = #{nextInfo.updateTime}
where 1 = 1

    and id = #{query.id}


    and version = #{query.version}


    and file_id = #{query.fileId}

mapper.select:
select *
from ws_file_pub
where
file_id = #{fileId}

    and type = #{type}

order by update_time desc
limit 1 offset 0;

select id, file_id, creator_id, create_time, update_time, version, type from ws_file_pub where file_id = #{fileId}
and type = #{type}
order by update_time desc limit #{limit} offset #{offset};
